import React, { useEffect, useState } from "react";
import Web3 from "web3";
import contractABI from "./abi/Voting.json"; // Ensure ABI is correctly updated

function App() {
  const [account, setAccount] = useState("");
  const [candidates, setCandidates] = useState([]);
  const [contract, setContract] = useState(null);
  const [web3, setWeb3] = useState(null);
  const CONTRACT_ADDRESS = "0x9e836b103ca160952044792977de0f2eb8841b47433088e93d497e3a8e2d31c2"; // Replace with your deployed contract address

  useEffect(() => {
    loadBlockchainData();
  }, []);

  // ✅ Load MetaMask and Smart Contract
  async function loadBlockchainData() {
    if (window.ethereum) {
      try {
        const web3Instance = new Web3(window.ethereum);
        setWeb3(web3Instance);

        // Request MetaMask account access
        const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
        setAccount(accounts[0]);

        // ✅ Load Smart Contract
        const contractInstance = new web3Instance.eth.Contract(contractABI, CONTRACT_ADDRESS);
        setContract(contractInstance);

        // ✅ Fetch Candidates from Blockchain
        fetchCandidates(contractInstance);

        // Listen for account changes
        window.ethereum.on("accountsChanged", (accounts) => {
          setAccount(accounts[0] || "");
        });
      } catch (error) {
        console.error("User denied account access or error in connecting:", error);
      }
    } else {
      alert("MetaMask not detected! Please install it.");
    }
  }

  // ✅ Fetch Candidates from Smart Contract
  async function fetchCandidates(contractInstance) {
    try {
      const totalCandidates = await contractInstance.methods.getTotalCandidates().call();
      console.log("Total Candidates:", totalCandidates); // Debugging
  
      let candidatesList = [];
      for (let i = 1; i <= totalCandidates; i++) { // Ensure IDs start from 1
        const candidate = await contractInstance.methods.getCandidate(i).call();
        console.log("Fetched Candidate:", candidate); // Debugging
  
        candidatesList.push({
          id: candidate[0],  // Candidate ID
          name: candidate[1], // Candidate Name
          voteCount: candidate[2] // Votes
        });
      }
  
      setCandidates(candidatesList);
    } catch (error) {
      console.error("Error fetching candidates:", error);
    }
  }
  

  // ✅ Voting Function
  async function vote(candidateId) {
    if (!account || !contract) {
      alert("Connect your MetaMask wallet first!");
      return;
    }

    try {
      await contract.methods.vote(candidateId).send({ from: account });
      alert("Vote successfully cast!");
      fetchCandidates(contract); // Refresh candidates after voting
    } catch (error) {
      console.error("Error submitting vote:", error);
      alert("Voting failed: " + error.message);
    }
  }

  return (
    <div>
      <h1>E-Voting System</h1>
      <button onClick={loadBlockchainData}>Connect MetaMask</button>
      <h3>Connected Account: {account || "Not connected"}</h3>

      {candidates.length > 0 ? (
        candidates.map((candidate) => (
          <div key={candidate.id}>
            <p>
              {candidate.name} - {candidate.voteCount} votes
            </p>
            <button onClick={() => vote(candidate.id)}>Vote</button>
          </div>
        ))
      ) : (
        <p>Loading candidates...</p>
      )}
    </div>
  );
}

export default App;
