import React, { useEffect, useState } from "react";
import Web3 from "web3";

function App() {
  const [account, setAccount] = useState("");
  const [candidates, setCandidates] = useState([]);
  const [web3, setWeb3] = useState(null);

  // Load candidates from the backend
  async function loadCandidates() {
    try {
      const response = await fetch("http://localhost:5001/api/candidates"); // Fixed API route
      const data = await response.json();
      setCandidates(data);
    } catch (error) {
      console.error("Error fetching candidates:", error);
    }
  }

  // Fetch candidates on component mount
  useEffect(() => {
    loadCandidates();
  }, []);

  // Connect MetaMask and listen for account changes
  async function loadMetaMask() {
    if (window.ethereum) {
      const web3Instance = new Web3(window.ethereum);
      setWeb3(web3Instance);

      try {
        const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
        setAccount(accounts[0]);
        loadCandidates();
      } catch (error) {
        console.error("User denied account access");
      }

      // Listen for account changes
      window.ethereum.on("accountsChanged", (accounts) => {
        setAccount(accounts[0] || "");
      });
    } else {
      alert("MetaMask not detected! Please install it.");
    }
  }

  // Voting function
  async function vote(candidateId) {
    if (!account) {
      alert("Connect your MetaMask wallet first!");
      return;
    }

    try {
      const response = await fetch("http://localhost:5001/api/vote", {  // Fixed API route
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ voterAddress: account, candidateId }),
      });

      const result = await response.json();
      if (result.success) {
        loadCandidates(); // Refresh candidates list after voting
      } else {
        alert("Voting failed: " + result.message);
      }
    } catch (error) {
      console.error("Error submitting vote:", error);
    }
  }

  return (
    <div>
      <h1>E-Voting System</h1>
      <button onClick={loadMetaMask}>Connect MetaMask</button>
      <h3>Connected Account: {account || "Not connected"}</h3>

      {candidates.length > 0 ? (
        candidates.map((candidate) => (
          <div key={candidate.id}>
            <p>{candidate.name} - {candidate.voteCount} votes</p>
            <button onClick={() => vote(candidate.id)}>Vote</button>
          </div>
        ))
      ) : (
        <p>Loading candidates...</p>
      )}
    </div>
  );
}

export default app;
